package week1.day2;

public class ConvertNegativeToPositive {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int num = -40;
		if(num<0)
		{
			num=num*(-1);
			System.out.println("The Given Negative Number -40 is converted to " +num+ " as a Positive Number");
		}

	}

}
