package week1.day2;

public class PositiveOrNegative {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		int num = -40;
		if(num>0)
		{
			
			System.out.println("The Given Number is"+ num+ " is POSITIVE");
		}
		else if(num<0)
		{
			System.out.println("The Given Number is"+ num+ " is NEGATIVE");
		}
		else
		{
			System.out.println("The Number is "+ num+ " is NEUTRAL ");
			
	}

}
}
